unset -v ol1 ol2; ol1=$(pastel random -n1|pastel desaturate .02|pastel darken .12); ol2="$(pastel complement $ol1|pastel format hex)"; (gum style --background "$(pastel format hex $ol1)" --foreground "$(pastel textcolor $ol1|pastel format hex)" --padding "1 2" "$(pastel format name $ol1; pastel format hex $ol1)" --align center --border hidden; gum style --foreground "$(pastel textcolor $ol2|pastel format hex)" --background "$ol2" --border hidden --padding "1 2" --align center "$(pastel format name $ol2; printf %b "$ol2")")| gum style --strip-ansi="no" --trim --align center --padding "2 12" --margin "1" --border hidden --background "$(pastel textcolor $ol1|pastel darken .2|pastel format hex)"|ansifilter -MF monospace -s 44 -c > spn.sh; sleep 1; convert pango:"$(cat spn.sh)" spn.jpg; chafa spn.jpg ;
